context.code:
// On all sides
#{ $prefix } {
  #{ $property } : units.get(1);
}
@each $name, $value in units.$config {
  #{ $prefix }-#{ $name } {
    #{ $property }: units.get($name);
  }
}
// Individual sides
@each $side in get("sides") {
  $full-prop: #{ $property }-#{ $side };
  #{ $prefix }-#{ $side } {
    #{ $full-prop }: units.get(1);
  }
  @each $name, $value in units.$config {
    #{ $prefix }-#{ $side }-#{ $name } {
      #{ $full-prop }: units.get($name);
    }
  }
}

// On all sides
#{ $prefix } {
  #{ $property } : units.get(1);
}
@each $name, $value in units.$config {
  #{ $prefix }-#{ $name } {
    #{ $property }: units.get($name);
  }
}
// Individual sides
@each $side in get("sides") {
  $full-prop: #{ $property }-#{ $side };
  #{ $prefix }-#{ $side } {
    #{ $full-prop }: units.get(1);
  }
  @each $name, $value in units.$config {
    #{ $prefix }-#{ $side }-#{ $name } {
      #{ $full-prop }: units.get($name);
    }
  }
}

@if (meta.type-of($value) == string) {
  @return utils.require-map-get($config, $value, 'unit [config]');
}
@return get("default") * $value;

