$prefix: selector.class("list-bullet");
#{ $prefix } {
  @include element.styles-unordered-list();
  li {
    &::marker {
      color: color.get("bullet");
    }
  }
}

$border: element.get-rule-style("light") !default;
.list-lines {
  list-style: none;
  margin: 0;
  padding: 0;
  border-top: $border;
  >li {
    border-bottom: $border;
    padding: 1em 0;
    >*:last-child {
      margin-bottom: 0;
    }
    &:last-child {
      border-bottom-width: 0;
    }
  }
}
.list-lines--dense {
  >li {
    padding: 0.5em 0;
    line-height: typography.get("line-height-dense");
  }
}

$class: selector.class("rule");

#{ $class } {
  @include element.rule();
}
// Output user's styles presets
@each $name, $value in element.$rule-styles {
  #{ $class }--#{ $name } {
    @include element.rule-style($name);
  }
}
// Output user's margin presets
@each $name, $value in element.$rule-margins {
  #{ $class }--margin-#{ $name } {
    @include element.rule-margin($name);
  }
}

// Meant to be user by tself without content
$short-width: get("short-width");
@if ($short-width) {
  #{ $class }--short {
    width: $short-width;
    max-width: 100%;
    border-bottom-width: get("short-border-width");
  }
}

