$prefix: selector.class("type");

// Print base typographic sizes
@each $size, $values in typography.$sizes {
  @if (map.get($values, "base-class")) {
    @if (map.get($values, "base-class-prefixed")) {
      #{ $prefix }-#{$size} {
        @include typography.size($size);
      }
    } @else {
      .#{$size} {
        @include typography.size($size);
      }
    }
  }
}

