$max: utils.require-map-get($sizes, $size-max, "breakpoints") - get("gap"); 
@media screen and (min-width: $min) and (max-width: $max) {
  @content;
}

&:before {
  display: none;
  content: get("null-name");
  @each $size, $breakpoint in $sizes {
    @include min($size) {
      content: $size;
    }
  }
}

@include utils.file-header('component', 'button');

$prefix: selector.class("button");
$icon-selector: "#{ $prefix }--icon";

#{ $prefix } {
  @include button.default();
}

@each $size, $values in button.$sizes {
  #{ $prefix }--#{$size} {
    @include button.size($size);
  }
}

@each $style, $values in button.$styles {
