  padding: 0;
  font-size: inherit;
  font-family: inherit;
  border-width: 0;
  border-style: solid;
  font-weight: inherit;
}
p {
  $margin-top: typography.get("margin-top");
  margin-top: if($margin-top, $margin-top, 0);
  margin-bottom: typography.get("margin-bottom"); 
}
sub, sup {
  font-size: 75%;
  line-height: 0;
  position: relative;
  vertical-align: baseline;
}
small { 
  @include typography.size("small", ("line-height" : typography.get("line-height-dense"))); 
}
sub { 
  bottom: -0.25em; 
}
sup { 
  top: -0.5em; 
}
b, 
strong { 
  font-weight: bold; 
}
code,
kbd,
samp,
pre {
