        );
        @include -create-rule-row-position(
          $position: "bottom", 
          $size: $size, 
          $attribute: $attribute, 
          $select-item: $select-item, 
          $select-rule-row: $select-rule-row, 
          $between: true
        );
      }
    }
  }
}

$attribute-item: "#{ $attribute }-item";
$select-grid: '[#{ $attribute }*="columns: #{ $columns }"]';
$select-item: '[#{ $attribute-item }]';
$select-rule-col: ":before";
$select-rule-row: ":after";
// These through off syntax highlighting when inside interpolation
$position-class-column-first: get("position-class-column-first");
$position-class-column-last: get("position-class-column-last");
$position-class-row-first: get("position-class-row-first");
$position-class-row-last: get("position-class-row-last");

#{ $select-grid } {
  @include layout.clearfix(); // For legacy browsers

  // Ensure empty elements still take up space in layout
  > #{ $select-item } {
    min-height: 1px; 
    width: 100%;
  }
  // This is for value of true and also covers all options below
  &#{'[#{ $attribute }*="gutters-row:"]'} { 
    > #{ $select-item } {
      padding-top:  $gutter;
      padding-bottom: $gutter;
    }
  }
  &#{'[#{ $attribute }*="gutters-row: top"]'} { 
    > #{ $select-item } {
      &.#{ $position-class-row-last } {
        padding-bottom: 0;
      }
    }
  }
  &#{'[#{ $attribute }*="gutters-row: bottom"]'} { 
    > #{ $select-item } {
      &.#{ $position-class-row-first } {
        padding-top: 0;
      }
    }
  }
  &#{'[#{ $attribute }*="gutters-row: fit"]'} { 
    margin-top: -$gutter;
    margin-bottom: -$gutter;
  }
  // Rules: Row
  &#{'[#{ $attribute }*="rules-row:"]'} { 
    > #{ $select-item } {
      &#{ $select-rule-row } {
        position: absolute;
        left: 0;
        right: 0;
        height: $rule-size;
        background: color.get($rule-color);
      }
    }
  }
  // Rules: Applies to both column and row
  &#{'[#{ $attribute }*="rules"]'} { 
    > #{ $select-item } {
      position: relative;
    }
  }
  // Rules: Column
  &#{'[#{ $attribute }*="rules-column:"]'} { 
    > #{ $select-item } {
      &#{ $select-rule-col } {
        position: absolute;
        top: 0;
        bottom: 0;
        width: $rule-size;
        background: color.get($rule-color);
      }
    }
  }
  // Options for the left or right
  @include -create-rule-col-position(
    $position: "left", 
    $size: $rule-size, 
    $attribute: $attribute, 
    $select-item: $select-item, 
    $select-rule-col: $select-rule-col, 
    $end-class: $position-class-column-first
  );
  @include -create-rule-col-position(
    $position: "right", 
    $size: $rule-size, 
    $attribute: $attribute, 
    $select-item: $select-item, 
    $select-rule-col: $select-rule-col, 
    $end-class: $position-class-column-last
  );
  // Override first or last column removing the rule using matching specificity
  &#{'[#{ $attribute }*="rules-column:"][#{ $attribute }*="rules-column-ends: true"]'} {
    > #{ $select-item } {
      &#{ $select-rule-col } {
        content: '';
      }
    }
  }
  &#{'[#{ $attribute }*="rules-column-align: gutter"]'} {
    > #{ $select-item } {
      &#{ $select-rule-col } {
        top: $gutter;
        bottom: $gutter;
      }
    }
  }
  // Options for the left or right
  @include -create-rule-row-position(
    $position: "top", 
    $size: $rule-size, 
    $attribute: $attribute, 
    $select-item: $select-item, 
    $select-rule-row: $select-rule-row, 
    $between: false
  );
  @include -create-rule-row-position(
    $position: "bottom", 
    $size: $rule-size, 
    $attribute: $attribute, 
    $select-item: $select-item, 
    $select-rule-row: $select-rule-row, 
    $between: false
  );

  &#{'[#{ $attribute }*="rules-row: between"]'} {
    > #{ $select-item } {
      &#{ $select-rule-row } {
        bottom: math.floor(math.div($rule-size, 2)) * -1;
      }
      // For removing the rule per row (via equal heights for now)
      &.#{ $position-class-row-last } {
        &#{ $select-rule-row } {
          content: none;
        }
      }
    }
  }
  &#{'[#{ $attribute }*="rules-row-align: gutter"]'} {
    > #{ $select-item } {
      &#{ $select-rule-row } {
        left: $gutter;
        right: $gutter;
      }
    }
  }
  &#{'[#{ $attribute }*="rules-row-persist: true"]'} {
    > #{ $select-item } {
      &#{ $select-rule-row } {
        content: '';
      }
    }
  }
}

@include breakpoint.min($breakpoint) {
  // Select the attribute with matching column count
  #{ $select-grid } {
    // Defaults for grid an item
    display: flex;
    flex-flow: row wrap;
    align-items: stretch;
    margin-left: -$gutter;
    margin-right: -$gutter;

    > #{ $select-item } {
      float: left; // Fallback
      padding-left: $gutter;
      padding-right: $gutter;
    }
    // Alignment options
    &#{'[#{ $attribute }*="align: center"]'} { 
      align-items: center;  
    }
    &#{'[#{ $attribute }*="align: end"]'} { 
      align-items: flex-end;
    }
    &#{'[#{ $attribute }*="align: start"]'} { 
      align-items: flex-start; 
    }
    // Alignment of the children when using stretch
    // - There is no stetch-start (that's defualt);
    // - There is no stetch-middle (that's covered by stretch-);
    &#{'[#{ $attribute }*="align: stretch-"]'} { 
      // set the child to display as a single flex item
      > #{ $select-item } {
        display: flex;
        flex-direction: column;
        justify-content: center;
      }
    }
    &#{'[#{ $attribute }*="align: stretch-end"]'} { 
      > #{ $select-item } {
        justify-content: flex-end;
      }
    }
    // Other options
    &#{'[#{ $attribute }*="gutters: false"]'} { 
      margin-left: 0;
      margin-right: 0;
      > #{ $select-item } {
        padding-left: 0;
        padding-right: 0;
      }
    }
    // Activate Rules
    &#{'[#{ $attribute }*="rules-column:"]'} {
      > #{ $select-item } {
        &#{ $select-rule-col } {
          content: '';
        }
      }
    }
    &#{'[#{ $attribute }*="rules-row:"]'} {
      > #{ $select-item } {
        &#{ $select-rule-row } {
          content: '';
        }
      }
    }
    // Generated props for item
    @include -create-column-widths(
      $columns: $columns,
      $attribute-item: $attribute-item,
      $suffix: ''
    );
    @include -create-column-offsets(
      $columns: $columns,
      $attribute-item: $attribute-item,
      $suffix: ''
    );
  }
}

@if $extra-breakpoints {
  @each $name, $options in $extra-breakpoints {
    $g: map.get($options, "gutter");
    @include -create-extra-breakpoint(
      $name: $name, 
      $breakpoint: map.get($options, "breakpoint"),
      $gutter: if($g, $g, $gutter),
      $columns: $columns,
      $attribute-item: $attribute-item,
      $select-grid: $select-grid,
      $select-item: $select-item
    );
  }
}
@if $extra-rule-styles {
  @each $name, $options in $extra-rule-styles {
    $color: utils.require-map-get($options, "color", "grid [extra rule style]");
    $size: utils.require-map-get($options, "size", "grid [extra rule style]");
    #{ $select-grid } {

      &#{'[#{_attr}*="rules-column-style: #{ $name }"]'} {
        > #{ $select-item } {
          &#{ $select-rule-col } {
            background-color: color.get($color);
            width: $size;
          }
        }
        @include -create-rule-col-position(
          $position: "left", 
          $size: $size, 
          $attribute: $attribute, 
          $select-item: $select-item, 
          $select-rule-col: $select-rule-col, 
          $end-class: false
        );
        @include -create-rule-col-position(
          $position: "right", 
          $size: $size, 
          $attribute: $attribute, 
          $select-item: $select-item, 
          $select-rule-col: $select-rule-col, 
          $end-class: false
        );
      }
      &#{'[#{_attr}*="rules-row-style: #{ $name }"]'} {
        > #{ $select-item } {
          &#{ $select-rule-row } {
            background-color: $color;
            height: $size;
          }
        }
        @include -create-rule-row-position(
          $position: "top", 
          $size: $size, 
          $attribute: $attribute, 
          $select-item: $select-item, 
          $select-rule-row: $select-rule-row, 
          $between: false
        );
        @include -create-rule-row-position(
          $position: "bottom", 
          $size: $size, 
          $attribute: $attribute, 
          $select-item: $select-item, 
          $select-rule-row: $select-rule-row, 
          $between: false
        );
        @include -create-rule-row-position(
          $position: "bottom", 
          $size: $size, 
          $attribute: $attribute, 
          $select-item: $select-item, 
          $select-rule-row: $select-rule-row, 
          $between: true
        );
      }
    }
  }
}

@if (meta.type-of($value) == string) {
  @return utils.require-map-get($config, $value, 'unit [config]');
}
@return get("default") * $value;

$index: list.index($list, $value) != null;
@if ($index == null) {
  @error 'ULU: Unable to find item "#{ $value }" in #{ $context } list.';
}

@each $item in $list {
  @include require-list-has($allowed, $item, $context);
}

@each $item in $list {
  @include require-list-has($allowed, $item, $context);
}

@if meta.type-of($value) == $type {
  @return $value;
} @else {
  @return $fallback;
}

$is-number: meta.type-of($number) != 'number';
$is-unitless: math.is-unitless($number);
@if ($is-unitless or not $is-number) {
  @return (
    "unit": null,
    "value": $number 
  );
} @else {
  @return (
    "unit" : math.unit($value), 
    "value": math.div($number, ($number * 0 + 1))
  );
}

@if $deep {
  @return map.deep-merge($original, $changes);
} @else if $overwrite {
  @return $changes;
} @else {
  @return map.merge($original, $changes);
}

// If there was already a value return it else resolve through passed function
@if ($value == true)  {
  $fn: map.get($lookup, $prop);
  @if ($fn) {
    @return meta.call($fn, $prop);
  } 
}
@return $value;
